  array('category_id' => '40','category_image' => 'shkatulki.jpg','category_parent_id' => '39','category_publish' => '1','category_ordertype' => '1','category_template' => NULL,'ordering' => '10','category_add_date' => '2014-03-20 06:59:22','products_page' => '12','products_row' => '3','access' => '1','name_en-GB' => '','alias_en-GB' => '','short_description_en-GB' => '','description_en-GB' => '','meta_title_en-GB' => '','meta_description_en-GB' => '','meta_keyword_en-GB' => '','name_ru-RU' => 'Шкатулки','alias_ru-RU' => '','short_description_ru-RU' => '- резьба, роспись и инкрустация','description_ru-RU' => '<h1>Эксклюзивные шкатулки на заказ</h1>
